import json
from azcausal.cloud.server import Server


def lambda_handler(event, _):

    if "body" in event:
        event = json.loads(event["body"])

        result = Server().handle(event)

        return {
            "isBase64Encoded": False,
            "statusCode": 200,
            "body": result,
            "headers": {
                "content-type": "application/json"
            }
        }

    else:
        return Server().handle(event)


